// GET VARIABLEN
let fct = flow.get('fct') || 0;

// FUNKTION
function fctDate() {
    const date = new Date();

    let d = date.getDate();
    d = ((d < 10) ? "0" + d : d);

    let mon = date.getMonth() + 1;
    mon = ((mon < 10) ? "0" + mon : mon);

    const y = date.getFullYear();

    let h = date.getHours();
    h = ((h < 10) ? "0" + h : h);

    let min = date.getMinutes();
    min = ((min < 10) ? "0" + min : min);

    let s = date.getSeconds();
    s = ((s < 10) ? "0" + s : s);

    fct = `${d}.${mon}.${y} ${h}:${min}:${s}`;
}

// SET VARIABLEN
flow.set('fct', fct);

// VARIABLEN UEBERGEBEN
msg.payload = fct;

// RETURN
return msg;

