function fctDate(fct) {
    const date = new Date(fct);

    let d = date.getDate();
    d = ((d < 10) ? "0" + d : d);

    let mon = date.getMonth() + 1;
    mon = ((mon < 10) ? "0" + mon : mon);

    const y = date.getFullYear();

    let h = date.getHours();
    h = ((h < 10) ? "0" + h : h);

    let min = date.getMinutes();
    min = ((min < 10) ? "0" + min : min);

    let s = date.getSeconds();
    s = ((s < 10) ? "0" + s : s);

    fct = `${d}.${mon}.${y} ${h}:${min}:${s}`;
    return fct;
}

// SET VARIABLEN
