// FUNKTION
function fctDate(date_value) {
    const date = new Date(date_value);

    let d = date.getDate();
    d = ((d < 10) ? "0" + d : d);

    let mon = date.getMonth() + 1;
    mon = ((mon < 10) ? "0" + mon : mon);

    const y = date.getFullYear();

    let h = date.getHours();
    h = ((h < 10) ? "0" + h : h);

    let min = date.getMinutes();
    min = ((min < 10) ? "0" + min : min);

    let s = date.getSeconds();
    s = ((s < 10) ? "0" + s : s);

    output = `${h}:${min}:${s} ${d}.${mon}.${y}`;
    return output;
}

msg.payload = fctDate(date_value);
return msg;

